 /// <summary>
 ///提示使用者輸入相關帳密資訊，並傳回其認證
 ///</summary>
 ///<returns>傳回的認證</returns>
public System.Net.NetworkCredential promptUserInformation()
{
    StringBuilder userPassword = new StringBuilder(), userID = new StringBuilder();
    // 設定相關變數
    CREDUI_INFO credUI = new CREDUI_INFO();
    credUI.cbSize = Marshal.SizeOf(credUI);
    bool save = false;
    CREDUI_FLAGS flags = CREDUI_FLAGS.ALWAYS_SHOW_UI | CREDUI_FLAGS.GENERIC_CREDENTIALS | CREDUI_FLAGS.SHOW_SAVE_CHECK_BOX;
    // 顯示視窗
    CredUIReturnCodes returnCode = AuthenticationPrompt.CredUIPromptForCredentials(
       ref credUI,
       "SomeServer", 
       IntPtr.Zero,
       0, //如果先前已認證過，也可以使用Marshal.GetLastWin32Error()來取得相關錯誤碼
       userID,
       100, 
       userPassword, 
       100,
       ref save,
       flags);
    if (returnCode == CredUIReturnCodes.NO_ERROR)
    {
        System.Security.SecureString securePassword = new System.Security.SecureString();
        for (int i = 0; i < userPassword.Length; i++)
        {
            securePassword.AppendChar(userPassword[i]);
        }
        //沒有任何錯誤發生
        System.Net.NetworkCredential networkCredential = new System.Net.NetworkCredential()
        {
            UserName = userID.ToString(),
            Password = userPassword.ToString(),
            SecurePassword = securePassword,
            Domain = "SomeServer"
        };
        return networkCredential;
    }
    return null;
}

 /// <summary>
 /// 提示使用者輸入相關帳密資訊，並傳回其認證
 /// </summary>
 /// <returns>傳回的認證</returns>
public System.Net.NetworkCredential promptUserInformation()
{
    CREDUI_INFO credui = new CREDUI_INFO();
    credui.pszCaptionText = String.Format("請輸入用於登入{0}的認證", " localhost");
    credui.pszMessageText = "登入";
    credui.cbSize = Marshal.SizeOf(credui);
    uint authPackage = 0;
    IntPtr outCredBuffer;
    uint outCredSize;
    bool save = false;
    CredUIReturnCodes result = AuthenticationPrompt.CredUIPromptForWindowsCredentials(
                                                   ref credui,
                                                   0,//如果先前已認證過，本參數也可以輸入Marshal.GetLastWin32Error()來取得並顯示相關錯誤碼
                                                   ref authPackage,
                                                   IntPtr.Zero,
                                                   0,
                                                   out outCredBuffer,
                                                   out outCredSize,
                                                   ref save,
                                                   CREDUIWIN.AUTHPACKAGE_ONLY | CREDUIWIN.CHECKBOX);

    StringBuilder usernameBuf = new StringBuilder(100);
    StringBuilder passwordBuf = new StringBuilder(100);
    StringBuilder domainBuf = new StringBuilder(100);

    int maxUserName = 100;
    int maxDomain = 100;
    int maxPassword = 100;
    if (result == CredUIReturnCodes.NO_ERROR)
    {
        //如果CredUIPromptForWindowsCredentials的Flag有設定CREDUIWIN.GENERIC，第一個參數必為0
        if (AuthenticationPrompt.CredUnPackAuthenticationBuffer(1, outCredBuffer, outCredSize, usernameBuf, ref maxUserName,
                                           domainBuf, ref maxDomain, passwordBuf, ref maxPassword))
        {
             //清除被CredUIPromptForWindowsCredentials所占用的記憶體
            AuthenticationPrompt.CoTaskMemFree(outCredBuffer);
            System.Security.SecureString securePassword = new System.Security.SecureString();
            for (int i = 0; i < passwordBuf.Length; i++)
            {
                securePassword.AppendChar(passwordBuf[i]);
            }
            System.Net.NetworkCredential networkCredential = new System.Net.NetworkCredential()
            {
                UserName = usernameBuf.ToString(),
                Password = passwordBuf.ToString(),
                Domain = domainBuf.ToString(),
                SecurePassword = securePassword
            };
            //取得帳密資訊完成
            return networkCredential;
        }
    }
    //其中有些地方有出錯
    return null;
}

/// <summary>
/// 模擬其他使用者的登入
/// </summary>
/// <param name="userName">使用者帳號</param>
/// <param name="domain">網域</param>
/// <param name="password">密碼</param>
/// <returns>是否驗證成功以及登入完成</returns>
public static bool impersonateValidUser(string userName, string domain, string password)
{
   if (WindowsImpersonation.impersonateValidUser(userName, domain, password, LogonType.LOGON32_LOGON_INTERACTIVE, LogonProvider.LOGON32_PROVIDER_DEFAULT))
   {
       //這裡所執行的程式碼都是由該模擬出來的人所執行的
        string impersonatedUSerName = WindowsIdentity.GetCurrent().Name;
       return true;
   }
   else
   {
       //認證失敗，這裡可以處理正失敗後的結果
       return false;
   }
}

