using System;
using TEC.Core.Settings.Collections;
namespace TEC.Core.TestConsole.Settings.Collections
{
    /// <summary>
    /// 測試快取設定檔集合
    /// </summary>
    public class SomeCachedSettingCollection :
        TEC.Core.Settings.Collections.CachedValueSettingCollectionBase<SomeCachedSettingEnum, object, string>
    {
        /// <summary>
        /// 初始化測試快取設定檔集合的物件
        /// </summary>
        public SomeCachedSettingCollection()
            : base("CachedCollectionId", System.Runtime.Caching.MemoryCache.Default) { }
        /// <summary>
        /// 當目前索引鍵無法對應到產生快取的方法封裝時，用於取得產生快取值的方法封裝
        /// </summary>
        /// <param name="key">要取得方法封裝的索引鍵</param>
        /// <returns>傳回產生 CachedValueConfig<object> 的方法封裝</returns>
        protected override Func<CachedValueConfig<object>> getSettingGenerateFunc(SomeCachedSettingEnum key)
        {
            //using TEC.Core.Settings.Collections;
            switch (key)
            {
                case SomeCachedSettingEnum.Setting1:
                    return () => new CachedValueConfig<object>(1, new System.Runtime.Caching.CacheItemPolicy()
                    {
                        //Setting1 在每30秒後過期一次
                        AbsoluteExpiration = DateTime.Now.AddSeconds(30d)
                    });
                case SomeCachedSettingEnum.Setting2:
                    return () => new CachedValueConfig<object>("Test", new System.Runtime.Caching.CacheItemPolicy()
                    {
                        //Setting2 在每15秒後過期一次
                        AbsoluteExpiration = DateTime.Now.AddSeconds(15d)
                    });
                case SomeCachedSettingEnum.Setting3:
                    return () =>
                    {
                        double value = new Random().NextDouble();
                        //複雜邏輯
                        return new CachedValueConfig<object>(value, new System.Runtime.Caching.CacheItemPolicy()
                        {
                            //Setting3 在每25秒後過期一次
                            AbsoluteExpiration = DateTime.Now.AddSeconds(25d)
                        });
                    };
                default:
                    throw new NotImplementedException();
            }
        }
    }
    /// <summary>
    /// 測試設定檔列舉
    /// </summary>
    public enum SomeCachedSettingEnum
    {
        /// <summary>
        /// Setting1, System.Int32
        /// </summary>
        Setting1,
        /// <summary>
        /// Setting1, System.String
        /// </summary>
        Setting2,
        /// <summary>
        /// Setting3, System.Double
        /// </summary>
        Setting3
    }
}

using System;
using System.IO;
using TEC.Core.Settings.Providers;
using TEC.Core.TestConsole.Settings.Collections;
namespace TEC.Core.TestConsole.Settings
{
    /// <summary>
    /// 用於產生設定檔的靜態類別
    /// </summary>
    public static class SettingCollectionFactory
    {
        /// <summary>
        /// 取得系統唯一的快取設定檔集合
        /// </summary>
        public static SomeCachedSettingCollection SomeCachedSettingCollection
        {
            get
            {
                ApplicationSettingProvider<SomeCachedSettingCollection, SomeCachedSettingEnum, object, string> provider =
                    new ApplicationSettingProvider<SomeCachedSettingCollection, SomeCachedSettingEnum, object, string>();
                if (provider.load() == null)
                {
                    provider.save(new SomeCachedSettingCollection());
                }
                return provider.load();
            }
        }
    }
}

